                <label for="userName">User Name: </label>
                <input id="userName" name="userName" placeholder="Enter your user name"></input>
                {/* <input type={"submit"} value={"Submit"}></input> */}
                <button onClick={handleSubmit}>Submit</button>
            </form>
        </div> 
    )
}

export default Home;
